Resources.SetScript:
$webpicmdexe = "$env:ProgramFiles\Microsoft\Web Platform Installer\WebPICmd.exe"
$webpicmdargs = @("/Install", "/Products:WDeploy", "/AcceptEula", "/SuppressReboot", "/Log:C:\ProgramData\Aditi\Brewmaster\WebDeploy-Install.log")
Write-Verbose "Installing WebDeploy V3 ($webpicmdexe $webpicmdargs)" -Verbose
Start-Process -FilePath $webpicmdexe -ArgumentList $webpicmdargs -Wait
if (!(Test-Path -LiteralPath "$env:ProgramFiles\IIS\Microsoft Web Deploy V3\msdeploy.exe" -PathType Leaf))
{
    Throw "$env:ProgramFiles\IIS\Microsoft Web Deploy V3\msdeploy.exe was not installed."
}

Resources.TestScript:
if (Test-Path -LiteralPath "$env:ProgramFiles\IIS\Microsoft Web Deploy V3\msdeploy.exe" -PathType Leaf)
{
    Write-Verbose "$env:ProgramFiles\IIS\Microsoft Web Deploy V3\msdeploy.exe already exists." -Verbose
    return $true
}
return $false

Args.Contents:
param(
	[string]$WebServers   # a space-separated list of server names
	)

function Write-Log
{
    $logfile = 'C:\ProgramData\Aditi\Brewmaster\WebFarm\WebDeploySyncContent.log'
    try
    {
        if (!(Test-Path -LiteralPath $logfile)) { 
            New-Item -Path $logfile -ItemType File -Force | Out-Null
        }

        $msg = $Args -join ' '
        $lineprefix = '{0:u}: ' -f (Get-Date)
        $msg = $msg -replace '(?m)^', $lineprefix
        $msg >> $logfile
    }
    catch
    {
        Write-Warning "Write-Log failed: $_"
    }
}

try
{
    $syncpath = 'C:\Inetpub\wwwroot'
    Write-Log ''
    Write-Log Synchronizing $syncpath
    $publishingServer = $env:computername
    foreach ($server in ((-split $WebServers) -ne $publishingServer))
    { 
        $exe = "$env:ProgramFiles\IIS\Microsoft Web Deploy V3\msdeploy.exe"
        $arguments = @("-verb:sync", "-source:contentPath=$syncpath,computerName=$publishingServer", "-dest:contentPath=$syncpath,computerName=$server")
        Write-Log Launching $exe "$arguments"
        & $exe $arguments 2>&1 | % { 
            if ($_ -is [System.Management.Automation.ErrorRecord]) { 
                Write-Log ($_ | Format-List -Force | out-string)
            }
            else {
                Write-Log $_
            }
        }
    }
    Write-Log Synchronization complete.
}
catch
{
    Write-Log Synchronization failed:
    Write-Log ($_ | Format-List * -Force | Out-String).Trim()
    throw
}

Resources.TestScript:
$task = Get-ScheduledTask -TaskName $TaskName -ErrorAction Ignore
if ($task -eq $null) { Write-Verbose "Task '$TaskName' not found."; return $false} 
if ($task.State -eq 'Disabled') { Write-Verbose "Task '$TaskName' is disabled."; return $false} 
if ($task.Actions -eq $null) { Write-Verbose "Task '$TaskName' has no action."; return $false} 
if ($task.Actions.Execute -ne $Execute -OR $task.Actions.Argument -ne $Argument) { Write-Verbose "Task '$TaskName' has different action."; return $false} 
if ($task.Triggers -eq $null -OR $task.Triggers.Enabled -contains $false) { Write-Verbose "Task '$TaskName' has no trigger."; return $false} 
if ($task.Principal.UserId -ne ([Security.Principal.WindowsIdentity]::GetCurrent().Name)) { Write-Verbose "Task '$TaskName' is running as different user."; return $false} 
Write-Verbose "Task '$TaskName' exists."
return $true
